<template>
<view>
 <u-modal :show="show" @close="$emit('close')" @cancel="$emit('close')" @confirm="onConfirm" title="头像与昵称" :confirm-text="$t('confirm')" :cancel-text="$t('cancel')" wConfirmButton showCancelButton="!force">
   <view class="slot-content flex flex-column" style="gap:32px">
      <view class="avatar" style="position: relative;">
          <button plain open-type="chooseAvatar" @chooseavatar="chooseAvatar">
              <u-avatar :src="userprofile.avatar" :size="64" style="height: 100%;" mode="widthFix"></u-avatar>
          </button>
