import api from '@/api';

export default {
 props: {
    show: {
        type: Boolean,
        default: false
    },
    force: {
      type: Boolean,
      default: false
    },
    newUser: {
      type: Boolean,
        default: true
    }
 },
 data() {
   return {
     userprofile: {
       nickname: null,
       avatar: null,
     }
   };
  },
  onLoad() {},
  watch: {
    show(v) {
      if (v) {
         this.getData()
      }
    }
 },		
 methods: {
   async onConfirm() {
    if (this.force && !this.userprofile.avatar) {
       uni.showToast({
           title: '请设置一个头像',
           icon: 'none'
       });
       return;
    }
    if (this.force && !this.userprofile.nickname) {
       uni.showToast({
           title: '请填写昵称',
           icon: 'none'
       });
      return;
    }
    uni.showLoading({
       //title: '正在更新',
       mask: true
    });
    try {
      await api.profile.updateNickname(this.userprofile.nickname);
      await api.profile.updateAvatar( this.userprofile.avatar);
      this.userprofile = await api.profile.getUserProfile();
         uni.showToast({
             title: this.$t('success'),
             icon: 'none'
         });
     } catch (e) {
         uni.showToast({
             title: this.$t('fail'),
             icon: 'none'
         });
     }
     this.$emit('confirm', {
         avatar: this.userprofile.avatar,
         nickname: this.userprofile.nickname
     })
   },
   getData() {
       api.profile.getUserProfile().then(res => {
           this.userprofile = res;
           if (this.newUser) {
               this.userprofile = {
                   avatar: null,
                   nickname: null,
               }
           }
       });
   },
   setNickname(v) {
     console.log(this.userprofile.nickname)
     //this.userprofile.nickname = v
   },
   chooseAvatar(e) {
      api.fileManagement.uploadFile(e.detail.avatarUrl, [Your server domain address]).then(res => {
         this.userprofile.avatar = res.downloadInfo.downloadUrl
      }).catch(err => {
        uni.showToast({
           title: this.$t('fail'),
           icon: 'none'
        });
      })
   },
  }
};
